I'm really liking the ability to embed processingjs right into a page.

<script type="application/processing">
// This sketch builds on a prior work, "Lines Marching", created by Doug Park
// http://studio.sketchpad.cc/sp/pad/view/ro.9lUgMi8$e$Zhk/rev.967

// This sketch builds on a prior work, "Lines of March", created by Doug Park
// http://studio.sketchpad.cc/sp/pad/view/ro.9o7Pwz5qhoLlT/rev.597

// height and width are built in global variables automatically set based on the canvas size.

// April Showers
// April 6, 2012
// Translating Circles

  // create a line counter to clear the screen every so often
  counter = 0;

// This runs once to setup the program
void setup() {

  // set canvis size to a big 500x500 pixel   
  size(500, 500);

  // make the background white 
  background(255); 

  // call a function to set the initial color of the circles
  changeColor();

  // setup the line
  x1 = random(width);
  y1 = random(height);
  r1 = random(15)+10;
  dx1 = random(11);
  dy1 = random(13);

  // set the width of the line. 
    strokeWeight(1);
}

// This runs in a continious loop
void draw() {

   // use translate to move the coordinates
   translate(x1,y1);

   // draw a circle 
   ellipse(1, 1, r1, r1);

   // Move the translation of the circle based on the delta offset
   x1 = x1 + dx1;
   y1 = y1 + dy1;


   // check if the point of the translation hits a screen boundry
   // if it does then reverse the delta offset for that translation
   // also change the color of the circle to keep things interesting
   if (x1 <= 0) { dx1 = - dx1; changeColor() };
   if (x1 >= width) { dx1 = - dx1; changeColor(); r1 = random(15)+10; };

   if (y1 <= 0) { dy1 = - dy1; changeColor() };
   if (y1 >= height) { dy1 = - dy1; changeColor(); r1 = random(15)+10; };

   // Clear the screen when mouse is pressed
   if (mousePressed == true) {
       background(255);
  }
}

// change the color of the line everytime an endpoint hits a screen boundry
void changeColor() {

   //stroke (random(50),random(255), random(255)); 

   // outline of the circle is white
   stroke (255,255,255);

   // fill the circle with a random color
   fill( random(50), random(255), random(255));

    }
</script>
